"use strict";

var PAGE = {
  data: {
    url: 'https://www.jevescript.com/api/task/mobile',
    phone: null
  },
  init: function init() {
    this.bind();
  },
  bind: function bind() {
    $('#apply-btn').on('click', this.judge);
    $('#apply-section').on('keyup', '#num-input', this.clean);
  },
  judge: function judge() {
    var value = $('#num-input').val().trim();
    var result = PAGE.isPhoneNumber(value);

    if (result) {
      PAGE.data.phone = value;
      PAGE.postTel();
    } else {
      PAGE.falseResult();
    }
  },
  isPhoneNumber: function isPhoneNumber(tel) {
    var reg = /^0?1[3|4|5|6|7|8][0-9]\d{8}$/;
    return reg.test(tel);
  },
  falseResult: function falseResult() {
    $('#false-num').css('display', 'block');
  },
  postTel: function postTel() {
    var phone = PAGE.data.phone;
    axios.post(PAGE.data.url, {
      phone: phone
    }).then(function (res) {
      if (res.data.code === 200) {
        $('#page').css('display', 'none');
        $('#success-page').css('display', 'block');
      }
    });
  },
  clean: function clean() {
    $('#false-num').css('display', 'none');
  }
};
PAGE.init();
